import React, { useState, useEffect, useContext } from 'react';
import { FileText } from 'lucide-react';
import { getProfile, updateProfile, downloadPDF, updateBio } from './API';  // Import updateBio here
import { AuthContext } from './AuthContext';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const { user, setUser } = useContext(AuthContext);
  const [selectedFile, setSelectedFile] = useState(null);
  const [bio, setBio] = useState('');
  const [message, setMessage] = useState('');
  const [showMessage, setShowMessage] = useState(false); // New state for message visibility
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        console.log("Fetching profile data...");
        if (!user) {
          const userData = await getProfile();
          setUser(userData);
          setBio(userData.bio || '');
        } else {
          setBio(user.bio || '');
        }
        console.log("Profile data fetched successfully.");
      } catch (error) {
        console.error('Error fetching profile data:', error);
        setMessage('Error fetching profile data.');
        navigate('/login');
      }
    };

    fetchProfileData();
  }, [user, setUser, navigate]);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type === 'application/pdf') {
      setSelectedFile(file);
      console.log(`Selected PDF: ${file.name}`);
    } else {
      alert('Please upload a valid PDF file.');
    }
  };

  const handleFileUpload = async () => {
    if (!selectedFile) {
      alert('No PDF selected.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await fetch('http://localhost:8000/upload_pdf', {
        method: 'POST',
        body: formData,
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const result = await response.json();
      if (response.ok) {
        setMessage('PDF uploaded successfully.');
        console.log("PDF uploaded successfully.");
      } else {
        console.error('Failed to upload PDF:', result);
        setMessage('Failed to upload PDF.');
      }
    } catch (error) {
      console.error('Error uploading PDF:', error);
      setMessage('Error uploading PDF.');
    }
  };

  const handlePDFDownload = async () => {
    try {
      console.log("Initiating PDF download...");
      await downloadPDF();
      setMessage('PDF downloaded successfully.');
    } catch (error) {
      console.error('Error downloading PDF:', error);
      setMessage('Failed to download PDF.');
    }
  };

  const handleBioUpdate = async () => {
    try {
      const result = await updateBio(bio);
      setMessage('Bio updated successfully.');
      setShowMessage(true); // Show the message
      setUser(prev => ({ ...prev, bio: result.bio }));

      // Hide the message after 3 seconds
      setTimeout(() => {
        setShowMessage(false);
      }, 3000);
    } catch (error) {
      console.error('Failed to update bio:', error);
      setMessage('Failed to update bio.');
      setShowMessage(true); // Show the message

      // Hide the message after 3 seconds
      setTimeout(() => {
        setShowMessage(false);
      }, 3000);
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className=" mx-auto items-center w-[60%] ">

    <div className=" container px-4 py-8 flex flex-col items-start h-auto min-h-[100px]">
      <h1 className="text-3xl font-bold mb-4">Your Profile</h1>
      <p className="text-base text-justify ">
        Welcome, {user.username}! Here you can view and edit your personal information. Please upload your CV and add a bio. Furthermore you can fill out preprepared Questions and add further information that can be set to be visible or unvisible to other users.
      </p>
      <div className="mt-4 rounded-lg w-full">
        <h2 className="text-xl font-semibold mt-4 mb-2">Bio:</h2> 
        <textarea
          value={bio}
          onChange={(e) => setBio(e.target.value)}
          className="w-full rounded-xl bg-white/50 p-4 placeholder-white text-black resize-y"
          rows={1}
          placeholder="Write something about yourself..."
        />
        <button onClick={handleBioUpdate} className="bg-white/50 text-black px-4 py-2 rounded-full mt-2 hover:bg-white/70 hover:scale-105 transition-all duration-300 ease-in-out">
          Update Bio
        </button>
      </div>

      {showMessage && (
        <p className="text-green-500 mt-2 transition-opacity duration-500 ease-in-out opacity-100">
          {message}
        </p>
      )}

      <div className="text-left  mt-8 w-52 h-auto">
        <h2 className="text-xl font-semibold mt-4 mb-4">Your CV</h2>  
        <label className="relative cursor-pointer">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="hidden"
          />
          <div className="flex flex-col items-center gap-2 p-4 border-2 border-dashed 
          border-gray-500 rounded-lg transition-transform transform 
          hover:scale-105 hover:border-blue-500 hover:text-blue-500 transition-all duration-300 ease-in-out group">
            <FileText
              size={24}
              className="text-gray-500 group-hover:text-blue-500 transition-all duration-300 ease-in-out"
            />
            <span className="text-sm font-medium text-gray-600 group-hover:text-blue-500 transition-all duration-300 ease-in-out">
              {selectedFile ? selectedFile.name : 'Click to upload PDF'}
            </span>
          </div>
        </label>
        <button onClick={handleFileUpload} className="bg-white/50 text-black px-4 py-2 rounded-full mt-2 hover:bg-white/70 hover:scale-105 transition-all duration-300 ease-in-out">
          Upload PDF
        </button>
        <button onClick={handlePDFDownload} className="bg-purple-500 text-white px-4 py-2 rounded mt-10">
          Download Uploaded PDF
        </button>
      </div>
    </div>
    </div>

  );
};

export default Profile;
