// src/components/Profile.js
import React, { useState } from 'react';
import { FileText, Twitter, Linkedin, Share2 } from 'lucide-react';

const Profile = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type === 'application/pdf') {
      setSelectedFile(file);
    } else {
      alert('Please upload a valid PDF file.');
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-4">Profile Page</h1>
      <p className="text-lg">
        Welcome to your profile! Here you can view and edit your personal information.
      </p>


      {/* Add more profile-related content here */}
      <div className="text-center mt-8 w-[50%]">
        <label className="relative cursor-pointer">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="hidden"
          />
          <div className="flex flex-col items-center gap-4 p-8 border-2 border-dashed border-gray-300 rounded-lg hover:border-blue-500 transition-colors">
            <FileText size={48} className="text-gray-800" />
            <span className="text-lg font-medium text-gray-600">
              {selectedFile ? selectedFile.name : 'Click to upload PDF'}
            </span>
          </div>
        </label>
      </div>
    </div>
  );
};

export default Profile;
