import React, { useState, useEffect, useContext } from 'react';
import { FileText, ChevronDown } from 'lucide-react';
import { FaCheckCircle } from 'react-icons/fa';
import { getProfile, updateProfile, downloadPDF, updateBio, checkPDF } from './API';  // Import updateBio here
import { AuthContext } from './AuthContext';
import { useNavigate } from 'react-router-dom';

// Dropdown options for user-added information
const dropdownOptions = [
  "List of 10 things you are looking (for help) with",
  "List of things you can offer other help with",
  "Your essay",
  "Books you have read",
  "Your explained CV",
  "List of things you want to have achieved in the next 6 months",
  "Where you have lived",
  "Your network of people",
  "Your fav philosopher",
  "Your fav book",
  "Your fav music artist",
  "Your fav artist",
  "Your fav visual artist",
  "Your fav movie",
  "Your hobbies",
  "Your lectures you have taken",
  "Coding experiences",
  "Languages you speak",
  "Languages you can code in and libraries you have used",
  "Custom"
];

const Profile = () => {
  const { user, setUser } = useContext(AuthContext);
  const [selectedFile, setSelectedFile] = useState(null);
  const [bio, setBio] = useState('');
  const [message, setMessage] = useState('');
  const [showMessage, setShowMessage] = useState(false);
  const [messageOpacity, setMessageOpacity] = useState(0); // State for opacity
  const [uploadedFileName, setUploadedFileName] = useState(''); // State for uploaded file name
  const [dropdownValue, setDropdownValue] = useState(''); // For the dropdown selection
  const [customFields, setCustomFields] = useState([]); // Store dynamic input fields
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // State for dropdown visibility
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        console.log("Fetching profile data...");
        if (!user) {
          const userData = await getProfile();
          setUser(userData);
          setBio(userData.bio || '');
        } else {
          setBio(user.bio || '');
        }

        // Check if a PDF is already uploaded
        const pdfCheck = await checkPDF();
        if (pdfCheck.has_pdf) {
          setUploadedFileName(pdfCheck.pdf_filename);  // Show the uploaded PDF filename if it exists
        }

        console.log("Profile data fetched successfully.");
      } catch (error) {
        console.error('Error fetching profile data:', error);
        setMessage('Error fetching profile data.');
        navigate('/login');
      }
    };

    fetchProfileData();
  }, [user, setUser, navigate]);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type === 'application/pdf') {
      setSelectedFile(file);
      console.log(`Selected PDF: ${file.name}`);
    } else {
      alert('Please upload a valid PDF file.');
    }
  };

  const handleFileUpload = async () => {
    if (!selectedFile) {
      alert('No PDF selected.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await fetch('http://localhost:8000/upload_pdf', {
        method: 'POST',
        body: formData,
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const result = await response.json();
      if (response.ok) {
        setMessage('PDF uploaded successfully.');
        setUploadedFileName(selectedFile.name); // Set the uploaded file name
        console.log("PDF uploaded successfully.");
      } else {
        console.error('Failed to upload PDF:', result);
        setMessage('Failed to upload PDF.');
      }
    } catch (error) {
      console.error('Error uploading PDF:', error);
      setMessage('Error uploading PDF.');
    }
  };

  const handlePDFDownload = async () => {
    try {
      console.log("Initiating PDF download...");
      await downloadPDF();
      setMessage('PDF downloaded successfully.');
    } catch (error) {
      console.error('Error downloading PDF:', error);
      setMessage('Failed to download PDF.');
    }
  };

  const handleBioUpdate = async () => {
    try {
      const result = await updateBio(bio);
      setMessage('Bio updated successfully.');
      setShowMessage(true);
      setMessageOpacity(1); // Set opacity to 1 for fade-in

      // Hide the message after 3 seconds
      setTimeout(() => {
        setMessageOpacity(0); // Set opacity to 0 for fade-out
        setTimeout(() => setShowMessage(false), 500); // Hide after fade-out duration
      }, 3000);
    } catch (error) {
      console.error('Failed to update bio:', error);
      setMessage('Failed to update bio.');
      setShowMessage(true);
      setMessageOpacity(1); // Set opacity to 1 for fade-in

      // Hide the message after 3 seconds
      setTimeout(() => {
        setMessageOpacity(0); // Set opacity to 0 for fade-out
        setTimeout(() => setShowMessage(false), 500); // Hide after fade-out duration
      }, 3000);
    }
  };

  // Handle adding a new field
  const handleAddField = () => {
    const title = dropdownValue === 'Custom' ? "Enter title for your information" : dropdownValue;
    setCustomFields([...customFields, { title, text: '', isPublic: true }]);
  };

  // Update a field's title, text, or toggle status
  const handleFieldChange = (index, key, value) => {
    const updatedFields = customFields.map((field, i) =>
      i === index ? { ...field, [key]: value } : field
    );
    setCustomFields(updatedFields);
  };

  // Handle dropdown toggle
  const handleToggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  // Handle dropdown selection
  const handleDropdownSelect = (option) => {
    setDropdownValue(option);
    setIsDropdownOpen(false);
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="mx-auto items-start w-[60%]">
      <div className="container px-4 py-8 flex flex-col items-start h-auto min-h-[100px]">
        <h1 className="text-3xl font-bold mb-4">Your Profile</h1>
        <p className="text-base text-justify ">
          Welcome, {user.username}! Here you can view and edit your personal information. Please upload your CV and add a bio.
        </p>

        <hr className="my-4 border-gray-800/20 border-[2px] w-full rounded-full mt-20" />  

        <div className="rounded-lg w-full">
          <h2 className="text-xl font-semibold  mb-2">Bio:</h2> 
          <textarea
            value={bio}
            onChange={(e) => setBio(e.target.value)}
            className="w-full rounded-xl bg-white/50 p-4 placeholder-white text-black resize-y hover:bg-white transition-all duration-300 ease-in-out"
            rows={1}
            placeholder="Write something about yourself..."
            style={{ minHeight: '100px', maxHeight: '300px' }}
          />
          <button onClick={handleBioUpdate} className="bg-white/50 text-sm text-black px-3 py-1 rounded-xl mt-2 hover:bg-white hover:scale-105 transition-all duration-300 ease-in-out">
            Update Bio
          </button>
        </div>

        {showMessage && (
          <p
            className={`text-green-500 mt-2 transition-opacity duration-500 ease-in-out`}
            style={{ opacity: messageOpacity }} // Control opacity with state
          >
            {message}
          </p>
        )}

        <hr className="my-4 border-gray-800/20 border-[2px] w-full rounded-full mt-20" />  

        <div className="text-left  w-52 h-auto">
          <h2 className="text-xl font-semibold mb-4">Your CV</h2>  
          <label className="relative cursor-pointer">
            <input
              type="file"
              accept=".pdf"
              onChange={handleFileChange}
              className="hidden"
            />
            <div className="flex flex-col items-center gap-2 p-4 border-2 border-dashed 
            border-gray-500 rounded-lg transition-transform transform 
            hover:scale-105 hover:border-blue-500 hover:text-blue-500 transition-all duration-300 ease-in-out group">
              <div className="flex items-center gap-1">
                <FileText
                  size={24}
                  className="text-gray-500 group-hover:text-blue-500 transition-all duration-300 ease-in-out"
                />
                {uploadedFileName && (
                  <FaCheckCircle size={20} className="text-green-500" /> /* Checkmark icon */
                )}
              </div>
              <span className="text-sm font-medium text-gray-600 group-hover:text-blue-500 transition-all duration-300 ease-in-out">
                {uploadedFileName ? (
                  `Click to replace: ${uploadedFileName}`
                ) : (
                  'Click to upload PDF'
                )}
              </span>
            </div>
          </label>
          <button onClick={handleFileUpload} className="bg-white/50 text-black text-sm text-black px-3 py-1 rounded-xl mt-2 hover:bg-white hover:scale-105 transition-all duration-300 ease-in-out">
            { 'Upload PDF'} {/* Change button text based on upload status */}
          </button>
        </div>

        <hr className="my-4 border-gray-800/20 border-[2px] w-full rounded-full mt-20" />  

        {/* Dropdown for custom information */}
        <div className="relative w-full">
          <div
            className="p-2 bg-white/50 text-black rounded-xl shadow-lg cursor-pointer flex items-center justify-between hover:scale-105 transition-all duration-300 ease-in-out"
            onClick={handleToggleDropdown}
          >
            <span className="font-semibold px-2 truncate">
              {dropdownValue || "Select Information Type"}
            </span>
            <ChevronDown
              className={`w-4 h-4 transition-transform duration-300 ${isDropdownOpen ? 'rotate-180' : ''}`}
            />
          </div>

          {isDropdownOpen && (
            <ul className="mt-2 bg-white/50 text-black rounded-xl shadow-lg max-h-60 overflow-y-auto">
              {dropdownOptions.map((option, index) => (
                <li
                  key={index}
                  className="px-4 py-2 cursor-pointer hover:bg-gray-500/10 transition-all duration-200"
                  onClick={() => handleDropdownSelect(option)}
                >
                  {option}
                </li>
              ))}
            </ul>
          )}

          <button 
            onClick={handleAddField} 
            className="bg-white/50 text-black text-sm px-3 py-1 rounded-xl mt-4 hover:bg-white hover:scale-105 transition-all duration-300 ease-in-out"
          >
            Add this info
          </button>
        </div>

        {/* Displaying added fields */}
        {customFields.map((field, index) => (
          <div key={index} className="mt-4 w-full">
            <div className="flex justify-between items-center">
              <input 
                type="text" 
                value={field.title}
                onChange={(e) => handleFieldChange(index, 'title', e.target.value)}
                className="w-4/5 p-2 mb-2 rounded-xl bg-white/50 text-black"
              />
              <button 
                className={`px-3 py-1 rounded-xl ${field.isPublic ? 'bg-green-500' : 'bg-red-500'} text-white`}
                onClick={() => handleFieldChange(index, 'isPublic', !field.isPublic)}
              >
                {field.isPublic ? 'Public' : 'Hidden'}
              </button>
            </div>
            <textarea 
              value={field.text}
              onChange={(e) => handleFieldChange(index, 'text', e.target.value)}
              className="w-full p-2 mb-2 rounded-xl bg-white/50 text-black"
              rows={2}
              placeholder="Enter details here..."
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Profile;
