import React, { useState, useEffect, useContext } from 'react';
import { FileText } from 'lucide-react';
import { getProfile, updateProfile } from './API';
import { AuthContext } from './AuthContext';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const { user, setUser } = useContext(AuthContext);
  const [selectedFile, setSelectedFile] = useState(null);
  const [bio, setBio] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        if (!user) {
          const userData = await getProfile();
          setUser(userData);
          setBio(userData.bio || '');
        } else {
          setBio(user.bio || '');
        }
      } catch (error) {
        setMessage('Error fetching profile data.');
        navigate('/login');
      }
    };

    fetchProfileData();
  }, [user, setUser, navigate]);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type === 'application/pdf') {
      setSelectedFile(file);
    } else {
      alert('Please upload a valid PDF file.');
    }
  };

  const handleFileUpload = async () => {
    if (!selectedFile) {
      alert('No PDF selected.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await fetch('http://localhost:8000/upload_pdf', {
        method: 'POST',
        body: formData,
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const result = await response.json();
      if (response.ok) {
        setMessage('PDF uploaded successfully.');
      } else {
        setMessage('Failed to upload PDF.');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
      setMessage('Error uploading file.');
    }
  };

  const handleBioUpdate = async () => {
    try {
      const updatedUser = await updateProfile({ bio });
      setUser(updatedUser);
      setMessage('Bio updated successfully.');
    } catch (error) {
      setMessage('Failed to update bio.');
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8 flex flex-col items-center">
      <h1 className="text-3xl font-bold mb-4">Profile Page</h1>
      <p className="text-lg text-center">
        Welcome, {user.username}! Here you can view and edit your personal information.
      </p>

      {/* Display and update bio */}
      <div className="mt-4 w-full max-w-md">
        <textarea
          value={bio}
          onChange={(e) => setBio(e.target.value)}
          className="w-full p-2 border rounded"
          rows={4}
          placeholder="Write something about yourself..."
        />
        <button onClick={handleBioUpdate} className="bg-blue-500 text-white px-4 py-2 rounded mt-2">
          Update Bio
        </button>
      </div>

      {message && <p className="text-green-500 mt-2">{message}</p>}

      {/* File upload */}
      <div className="text-center mt-8 w-52 h-52">
        <label className="relative cursor-pointer">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="hidden"
          />
          <div className="flex flex-col items-center gap-4 p-8 border-2 border-dashed border-gray-500 rounded-lg transition-transform transform hover:scale-105 hover:border-blue-500 hover:text-blue-500 transition-all duration-300 ease-in-out group">
            <FileText
              size={48}
              className="text-gray-500 group-hover:text-blue-500 transition-all duration-300 ease-in-out"
            />
            <span className="text-lg font-medium text-gray-600 group-hover:text-blue-500 transition-all duration-300 ease-in-out">
              {selectedFile ? selectedFile.name : 'Click to upload PDF'}
            </span>
          </div>
        </label>
        <button onClick={handleFileUpload} className="bg-green-500 text-white px-4 py-2 rounded mt-4">
          Upload PDF
        </button>
      </div>
    </div>
  );
};

export default Profile;
